#!/bin/sh
# Generates the colors for alacritty config

LIGHT_LOW=6
LIGHT_HIGH=b
NORMAL_LOW=4
NORMAL_HIGH=8

BG=f
FG=0

OUT_FILE=colors.toml

LIGHT_LOW=$LIGHT_LOW$LIGHT_LOW
LIGHT_HIGH=$LIGHT_HIGH$LIGHT_HIGH
LIGHT_FG=$LIGHT_FG$LIGHT_FG
NORMAL_LOW=$NORMAL_LOW$NORMAL_LOW
NORMAL_HIGH=$NORMAL_HIGH$NORMAL_HIGH
DARK_LOW=$DARK_LOW$DARK_LOW
DARK_HIGH=$DARK_HIGH$DARK_HIGH
DARK_FG=$DARK_FG$DARK_FG
BG=$BG$BG
FG=$FG$FG

cat > $OUT_FILE << EOF
# [WARNING] This is an autogenerated file from $0

[colors.selection]
text = "CellForeground"
background = 0x$LIGHT_LOW$LIGHT_LOW$LIGHT_LOW

[colors.bright]
black = 0x$LIGHT_LOW$LIGHT_LOW$LIGHT_LOW
white = 0x$LIGHT_HIGH$LIGHT_HIGH$LIGHT_HIGH
red = 0x$LIGHT_HIGH$LIGHT_LOW$LIGHT_LOW
green = 0x$LIGHT_LOW$LIGHT_HIGH$LIGHT_LOW
blue = 0x$LIGHT_LOW$LIGHT_LOW$LIGHT_HIGH
cyan = 0x$LIGHT_LOW$LIGHT_HIGH$LIGHT_HIGH
magenta = 0x$LIGHT_HIGH$LIGHT_LOW$LIGHT_HIGH
yellow = 0x$LIGHT_HIGH$LIGHT_HIGH$LIGHT_LOW

[colors.normal]
black = 0x$NORMAL_LOW$NORMAL_LOW$NORMAL_LOW
white = 0x$NORMAL_HIGH$NORMAL_HIGH$NORMAL_HIGH
red = 0x$NORMAL_HIGH$NORMAL_LOW$NORMAL_LOW
green = 0x$NORMAL_LOW$NORMAL_HIGH$NORMAL_LOW
blue = 0x$NORMAL_LOW$NORMAL_LOW$NORMAL_HIGH
cyan = 0x$NORMAL_LOW$NORMAL_HIGH$NORMAL_HIGH
magenta = 0x$NORMAL_HIGH$NORMAL_LOW$NORMAL_HIGH
yellow = 0x$NORMAL_HIGH$NORMAL_HIGH$NORMAL_LOW

[colors.primary]
foreground = 0x$FG$FG$FG
background = 0x$BG$BG$BG

[bell]
color = 0x$NORMAL_LOW$NORMAL_LOW$NORMAL_LOW
EOF

sed -i 's/0x.*$/"&"/g' $OUT_FILE
